pragma solidity ^0.8.0;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

interface IAgentLaunchpad {
    event TokenCreated(
        address indexed token,
        address indexed creator,
        string name,
        string symbol,
        uint256 limitPerWallet,
        uint256 goal,
        uint256 duration,
        string metadata,
        address bondingCurve,
        bytes32 salt
    );

    struct CreateParams {
        string name;
        string symbol;
        uint256 duration;
        uint256 limitPerWallet;
        uint256 goal;
        string metadata;
        address locker;
        address txChecker;
        address bondingCurve;
        bytes32 salt;
    }

    function initialize(IERC20 _raiseToken, address _owner) external;

    function setSettings(
        uint256 _creationFee,
        uint256 _minFundingGoal,
        uint256 _minDuration,
        uint256 _maxDuration,
        address _locker,
        address _checker,
        address _governor
    ) external;

    function whitelist(address _address, bool _what) external;

    function create(CreateParams memory p) external;

    function getTotalTokens() external view returns (uint256);
}
